SELECT IncidentNo AS IncidentNo FROM tblActiveFireCalls WHERE tblActiveFireCalls.IncDate >= @__TIMESTAMP__ UNION SELECT Incident AS IncidentNo FROM tblActiveCallNotes LEFT JOIN tblActiveFireCalls ON tblActiveFireCalls.IncidentNo = tblActiveCallNotes.Incident WHERE CONVERT(VARCHAR, CONVERT(DATETIME, CONVERT(VARCHAR(10), tblActiveFireCalls.IncDate, 111) + ' ' + CONVERT(CHAR(8), tblActiveCallNotes.NoteTime, 108) ), 126) >= @__TIMESTAMP__ UNION SELECT Incident AS IncidentNo FROM tblActiveTrucks WHERE Dispatched >= @__TIMESTAMP__ OR Enroute >= @__TIMESTAMP__ OR OnScene >= @__TIMESTAMP__ OR Available >= @__TIMESTAMP__ OR AvailOnScene >= @__TIMESTAMP__
