SELECT t1.CALLKEY, MAX( t3.SEGMENT_SEQ_CTRL ) AS NEWEST_ENTRY FROM CARSCALL t1 LEFT JOIN CARSCALLUNIT t2 ON t1.CALLKEY = t2.CALLKEY LEFT JOIN CARSSEGMENTS t3 ON t1.CALLKEY = t3.CALLKEY WHERE t1.CALL_CREATED_DATE > DATEADD(day, -5, GETDATE()) AND t1.SERVICE IN ('F', 'E') GROUP BY t1.CALLKEY HAVING DATEDIFF(second, DATEADD(ss, MAX( t3.SEGMENT_INT ), '1970-01-01'), CURRENT_TIMESTAMP) <= 180;